n	sli.click(function(){\r\n		//inow=;\r\n		$("#sheb_con").find("li").hide(1000);\r\n		$("#sheb_con").find("li").eq(sli.index(this)).show(1000);\r\n		sli.removeClass("now").eq(sli.index(this)).addClass("now");\r\n		});\r\n});\r\n</script>\r\n[litag]dynamic.artInCols(1,12,10,0,0,0,0,''isUse="true"'',''orderBy desc'',''[xm_c1,xm_t1,xm_con1],[xm_c2,xm_t2,xm_con2]'')[/litag]\r\n[litag]temp.footer[/litag]\r\n[litag]temp.zixunqq[/litag]\r\n[litag]temp.footjs[/litag]\r\n</body>\r\n</html>\r\n', NULL, '2019-04-15 16:52:33'),
